er_siteaccount_form"                                      data-action="add_new_customer_siteaccount" >                                    <input type="hidden" name="@customer_siteaccount_fields$customer_id" value="<?= $customer_id ?>"/>                                    <input type="hidden" name="@customer_siteaccount_fields$re_password" value="1"/>                                    <input type="hidden" name="@customer_siteaccount_fields$re_password_code" value="<?= rand(999, 9999); ?>"/>                                    <div class="form-body">                                        <div class="mail_form">                                            <?php                                            $template = [];                                            $row = [];                                            $col = [];                                            if (!empty($customer_mail_data)) {                                                $radio = [];                                                foreach ($customer_mail_data as $data) {                                                    if ($data->confirm == 0) {                                                        $ex = '<i class="fa fa-exclamation" style="color:red" title="Mail adresi onaylı değil"></i>';                                                    }                                                    $radio[] = ["text" => $data->customer_mail . " " . $ex, "attributes" => ["class" => "md-check", "value" => $data->customer_mail]];                                                }                                                echo '   <p>Kullanıcı hesapları için bir mail hesabı seçin</p>';                                                $col[] = [                                                    "col" => ["3"],                                                    "content" =>                                                    [                                          