                            </div>                                        </div>                                    <?php endif; ?>                                <?php endforeach; ?>                            <?php endif; ?>                        </li>                    <?php endforeach; ?>                    <li class="list-group-item">                        <div class="row">                            <div class="col-sm-8">Toplam </div>                            <div class="col-sm-4"><strong><?= number_format($job->job_total_price, 2) ?> <?= strtoupper($job->product_currency) ?></strong></div>                        </div>                    </li>                </ul>            </div>        </div>    </div><?php endif; ?><?php component::end(); ?>